function submitBusForm(){
	if(PlatUtil.isFormValid("#baseform")){
		var url = $("#baseform").attr("action");
		var formData = PlatUtil.getFormEleData("baseform");
		PlatUtil.ajaxProgress({
			url:url,
			params : formData,
			callback : function(resultJson) {
				if (resultJson.success) {
             if(resultJson.msg){
               parent.layer.msg(PlatUtil.SUCCESS_MSG, {icon: 1});
             }else{
               parent.layer.msg(PlatUtil.SUCCESS_MSG, {icon: 1});
             }
					PlatUtil.setData("submitSuccess",true);
					PlatUtil.closeWindow();
				} else {
             if(resultJson.msg){
               parent.layer.alert(resultJson.msg,{icon: 2,resize:false});
             }else{
               parent.layer.alert(PlatUtil.FAIL_MSG,{icon: 2,resize:false});
             }

				}
			}
		});
	}
}

function validateInfo(){
	if(PlatUtil.isFormValid("#baseform")){
		var url = $("#baseform").attr("action");
		var formData = PlatUtil.getFormEleData("baseform");
		PlatUtil.ajaxProgress({
			url:"appmodel/DbConnController.do?isValid",
			params : formData,
			callback : function(resultJson) {
				if (resultJson.success) {
					parent.layer.msg("验证通过!", {icon: 1});
				} else {
					parent.layer.alert("验证失败!",{icon: 2,resize:false});
				}
			}
		});
	}
}

function delJqGridRecordInfo(){
	PlatUtil.operMulRecordForTable({
		tableId:"baselist_datatable",
		selectColName:"DBCONN_ID",
		url:"appmodel/DbConnController.do?multiDel",
		callback:function(resultJson){
			$("#baselist_datatable").trigger("reloadGrid"); 
		}
	});
}

function editJqGridRecordInfo(){
	var rowData = PlatUtil.getTableOperSingleRecord("baselist_datatable");
	if(rowData){
		addJqGridRecordInfo(rowData.DBCONN_ID);
	}
}

function addJqGridRecordInfo(DBCONN_ID){
	var url = "appmodel/DbConnController.do?goForm&UI_DESIGNCODE=dbconnform";
	var title = "新增数据源信息信息";
	if(DBCONN_ID){
		url+=("&DBCONN_ID="+DBCONN_ID);
		title = "编辑数据源信息信息";
	}
	PlatUtil.openWindow({
	  title:title,
	  area: ["800px","400px"],
	  content: url,
	  end:function(){
		  if(PlatUtil.isSubmitSuccess()){
              //弹出框提交成功后,需要回调的代码
			  $("#baselist_datatable").trigger("reloadGrid"); 
		  }
	  }
	});
}
