function submitBusForm(){
	if(PlatUtil.isFormValid("#baseform")){
		var url = $("#baseform").attr("action");
		var formData = PlatUtil.getFormEleData("baseform");
      var FILES_JSON = PlatUtil.getMultiFileWebUploadValue("picker");
      if(FILES_JSON!=""){
          var fileList = $.parseJSON(FILES_JSON);
          if(fileList.length>1){
            parent.layer.alert("只能上传一个JAR文件!",{icon: 2,resize:false});
          }else{
            formData.FILES_JSON= FILES_JSON;
            PlatUtil.ajaxProgress({
              url:url,
              params : formData,
              callback : function(resultJson) {
                  if (resultJson.success) {
                       if(resultJson.msg){
                         parent.layer.msg(PlatUtil.SUCCESS_MSG, {icon: 1});
                       }else{
                         parent.layer.msg(PlatUtil.SUCCESS_MSG, {icon: 1});
                       }
                      PlatUtil.setData("submitSuccess",true);
                      PlatUtil.closeWindow();
                  } else {
                       if(resultJson.msg){
                         parent.layer.alert(resultJson.msg,{icon: 2,resize:false});
                       }else{
                         parent.layer.alert(PlatUtil.FAIL_MSG,{icon: 2,resize:false});
                       }

                  }
              }
          	});
          }

      }else{
        parent.layer.alert("必须上传JAR文件!",{icon: 2,resize:false});
      }

	}
}

function addJqGridRecordInfo(DEPLOYLOG_ID){
	var url = "system/DeployLogController.do?goForm&UI_DESIGNCODE=deploylog_form";
	var title = "新增部署记录信息";
	if(DEPLOYLOG_ID){
		url+=("&DEPLOYLOG_ID="+DEPLOYLOG_ID);
		title = "查看部署记录信息";
	}
	PlatUtil.openWindow({
	  title:title,
	  area: ["800px","400px"],
	  content: url,
	  end:function(){
		  if(PlatUtil.isSubmitSuccess()){
              //弹出框提交成功后,需要回调的代码
			  $("#baselist_datatable").trigger("reloadGrid"); 
		  }
	  }
	});
}

