function editTreeInfoWin(){
	PlatUtil.hideZtreeRightMenu();
	var selectTreeNode = $.fn.zTree.getZTreeObj("basetree").getSelectedNodes()[0];
	var CATALOG_ID = selectTreeNode.id;
	if(CATALOG_ID=="0"){
		parent.layer.alert("根节点不能进行编辑!",{icon: 2,resize:false});
	}else{
		showTreeInfoWin(CATALOG_ID);
	}
}

function showTreeInfoWin(CATALOG_ID) {
	var url = "metadata/DataCatalogController.do?goForm&UI_DESIGNCODE=catalog_form";
	var title = "新增数据目录";
	if(CATALOG_ID){
		title = "编辑数据目录";
		url+=("&CATALOG_ID="+CATALOG_ID);
	}else{
		PlatUtil.hideZtreeRightMenu();
		var selectTreeNode = $.fn.zTree.getZTreeObj("basetree").getSelectedNodes()[0];
		var CATALOG_ID = selectTreeNode.id;
		url+=("&DATACATALOG_PARENTID="+CATALOG_ID);
	}
	PlatUtil.openWindow({
		title:title,
		area: ["800px","200px"],
		content: url,
		end:function(){
		  if(PlatUtil.isSubmitSuccess()){
			  $.fn.zTree.getZTreeObj("basetree").reAsyncChildNodes(null, "refresh");
		  }
		}
	});
}
