function delJqGridRecordInfo(){
	PlatUtil.operMulRecordForTable({
		tableId:"sysuser_datatable",
		selectColName:"SYSUSER_ID",
		url:"system/SysUserController.do?multiDel",
		callback:function(resultJson){
